import 'package:flutter/material.dart';import 'package:levycon/model/filter.dart';class FilterList extends StatefulWidget {  @override  _FilterListState createState() => _FilterListState();}class _FilterListState extends State<FilterList> {  List<Filter> allTiker = [    Filter(tiker: 'Starter'),    Filter(tiker: 'All'),    Filter(tiker: 'Briyani'),    Filter(tiker: 'Burger'),    Filter(tiker: 'Pizza'),  ];  @override  void initState() {    allTiker[1].pressed = true;    // TODO: implement initState    super.initState();  }  @override  Widget build(BuildContext context) {    return Container(        height: MediaQuery.of(context).size.height * .05,        width: MediaQuery.of(context).size.width * .1,        child: ListView.builder(          scrollDirection: Axis.horizontal,          itemCount: allTiker.length,          itemBuilder: (BuildContext context, index) {            return Container(              margin: EdgeInsets.symmetric(horizontal: 10, vertical: 10),              height: MediaQuery.of(context).size.height * .04,              width: MediaQuery.of(context).size.width * .17,              child: InkWell(                onTap: () {                  setState(() {                    allTiker[index].pressed = ! allTiker[index].pressed;                  });                },                child: Container(                  key: Key(index.toString()),                  color: allTiker[index].pressed                      ? Colors.yellow                      : Colors.grey[200],                  child: Center(                      child: Text(                    '${allTiker[index].tiker}',                    textAlign: TextAlign.center,                    style: TextStyle(fontWeight: FontWeight.bold, fontSize: 10),                  )),                ),              ),            );          },        ));  }}